import { Link } from "react-router-dom"
import { useRef, useState, useEffect } from 'react';
// export default function Login() {
//     return (
//         <Link to="/" style={{ textDecoration: 'none', color: 'inherit', cursor: 'default' }}>
//             {/* <body>
//                 <div className="container">
//                     <img width="450" height="600" src="	https://www.instagram.com/static/images/homepage/phones/home-phones.png/1dc085cdb87d.png"></img>
//                     <div className="login" >
//                         <img width="185" height="70" src="./image/pngegg.png"></img>
//                         <input placeholder="  전화번호, 사용자 이름 또는 이메일" />
//                         <input placeholder="  비밀번호" /><br></br>
//                         <button>로그인</button>
//                     </div>

//                 </div>
//             </body> */}

//         </Link>

//     )
// }
const Login = () => {
    const userRef = useRef();
    const errRef = useRef();

    const [user, setUser] = useState('');
    const [pwd, setPwd] = useState('');
    const [errMsg, setErrMsg] = useState('');
    const [success, setSuccess] = useState(false);

    useEffect(() => {
        userRef.current.focus();

    }, [])
    useEffect(() => {
        setErrMsg('');
    }, [user, pwd])
    return (
        <section>
            <p ref={errRef} className={errMsg ? "errmsg" :
                "offscreen"} aria-live="assertive">{errMsg}</p>
            <h1>Sign In</h1>
            <form>
                <label htmlFor="username">Username:</label>
                <input type="text"
                    id="username"
                    ref={userRef}
                    autoComplete="off"
                    onChange={(e) => setUser(e.target.value)}
                    value={user} 
                    required
                />
                <label htmlFor="password">Password:</label>
                <input type="password"
                    id="password"
                    ref={userRef}
                    autoComplete="off"
                    onChange={(e) => setPwd(e.target.value)}
                    value={pwd} 
                    required
                />
            </form>
        </section>
    )
}

export default Login
